import { useEffect, useState } from "react";
import api from "../services/api";

export default function Dashboard(){
  const [items,setItems]=useState([]);
  const [type,setType]=useState("");

  useEffect(()=>{ load(); },[type]);

  const load = async()=>{
    const res = await api.get("/items"+(type?`?type=${type}`:""));
    setItems(res.data);
  };

  return (
    <div className="p-6">
      <select className="border p-2" onChange={e=>setType(e.target.value)}>
        <option value="">All</option>
        <option value="lost">Lost</option>
        <option value="found">Found</option>
      </select>

      <div className="grid grid-cols-3 gap-4 mt-4">
        {items.map(i=>(
          <div key={i._id} className="border p-3 rounded">
            {i.image && <img src={`http://localhost:5000/${i.image}`} className="h-40 w-full object-cover"/>}
            <h3 className="font-bold">{i.title}</h3>
            <p>{i.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
