import { useParams, Link } from "react-router-dom";
import { useEffect, useState } from "react";
import api from "../services/api";
import toast from "react-hot-toast";

export default function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadItem = async () => {
      try {
        const res = await api.get(`/items/public/${id}`);
        setItem(res.data);
      } catch {
        toast.error("Item not available");
      } finally {
        setLoading(false);
      }
    };

    loadItem();
  }, [id]);

  if (loading) {
    return <p className="p-6">Loading item...</p>;
  }

  if (!item) {
    return <p className="p-6">Item not found</p>;
  }

  return (
    <div className="max-w-4xl mx-auto p-6 animate-fadeInUp">

      <Link
        to="/dashboard"
        className="text-blue-600 hover:underline mb-4 inline-block"
      >
        ‚Üê Back to Dashboard
      </Link>

      <div className="bg-white rounded-xl shadow p-6">

        {item.image && (
          <img
            src={`http://localhost:5000/${item.image}`}
            alt={item.title}
            className="w-full h-80 object-cover rounded mb-6"
          />
        )}

        <div className="flex items-center gap-3 mb-3">
          <h1 className="text-3xl font-extrabold">{item.title}</h1>
          <span
            className={`px-3 py-1 text-xs font-bold rounded-full
              ${
                item.type === "lost"
                  ? "bg-red-100 text-red-700"
                  : "bg-green-100 text-green-700"
              }`}
          >
            {item.type.toUpperCase()}
          </span>
        </div>

        <p className="text-gray-600 mb-4">{item.description}</p>

        <div className="border-t pt-4 mt-4">
          <p className="font-semibold text-gray-700">Contact</p>
          <p className="text-gray-600">{item.contact}</p>
        </div>
      </div>
    </div>
  );
}
