import { useEffect, useState } from "react";
import api from "../services/api";

export default function Admin() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  const load = () => {
    api.get("/items")
      .then(res => {
        setItems(res.data);
        setLoading(false);
      });
  };

  useEffect(load, []);

  const approve = async (id) => {
    await api.put(`/items/${id}/approve`);
    load();
  };

  const remove = async (id) => {
    if (!window.confirm("Delete this item?")) return;
    await api.delete(`/items/${id}`);
    load();
  };

  const pending = items.filter(i => i.status === "pending").length;
  const approved = items.filter(i => i.status === "approved").length;

  return (
    <div className="min-h-screen bg-gray-100 p-6">

      {/* Header */}
      <div className="bg-white p-6 rounded-xl shadow mb-6 animate-fadeInUp">
        <h1 className="text-3xl font-extrabold mb-2">Admin Panel</h1>
        <p className="text-gray-600">
          Moderate and manage all items
        </p>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <Stat title="Total Items" value={items.length} />
        <Stat title="Pending Approval" value={pending} color="yellow" />
        <Stat title="Approved Items" value={approved} color="green" />
      </div>

      {/* Items */}
      <div className="bg-white rounded-xl shadow p-4 animate-fadeInUp">
        <h2 className="text-xl font-bold mb-4">Items</h2>

        {loading ? (
          <p className="text-gray-500">Loading...</p>
        ) : (
          <div className="space-y-3">
            {items.map(item => (
              <div
                key={item._id}
                className="border rounded-lg p-4 flex justify-between items-center
                  hover:shadow-lg transition animate-cardFade"
              >
                <div>
                  <h3 className="font-bold">{item.title}</h3>
                  <p className="text-sm text-gray-500">
                    {item.type} â€¢ {item.status}
                  </p>
                </div>

                <div className="flex gap-2">
                  {item.status === "pending" && (
                    <button
                      onClick={() => approve(item._id)}
                      className="px-4 py-1 bg-green-500 text-white rounded
                        hover:bg-green-600 active:scale-95 transition"
                    >
                      Approve
                    </button>
                  )}

                  <button
                    onClick={() => remove(item._id)}
                    className="px-4 py-1 bg-red-500 text-white rounded
                      hover:bg-red-600 active:scale-95 transition"
                  >
                    Delete
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

/* Stats Card */
function Stat({ title, value, color = "blue" }) {
  const map = {
    blue: "bg-blue-50 text-blue-600",
    green: "bg-green-50 text-green-600",
    yellow: "bg-yellow-50 text-yellow-600"
  };

  return (
    <div
      className={`p-5 rounded-xl shadow animate-fadeInUp ${map[color]}`}
    >
      <p className="text-sm font-medium">{title}</p>
      <p className="text-3xl font-extrabold mt-2">{value}</p>
    </div>
  );
}
