import { useEffect, useState } from "react";
import api from "../services/api";
import toast from "react-hot-toast";

export default function Admin() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadClaims = async () => {
    try {
      const res = await api.get("/items/claims/all");
      setItems(res.data);
    } catch {
      toast.error("Failed to load claims");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadClaims();
  }, []);

  const approveClaim = async (itemId, index) => {
    try {
      await api.put(`/items/${itemId}/claim/${index}/approve`);
      toast.success("Claim approved");
      loadClaims();
    } catch {
      toast.error("Failed to approve claim");
    }
  };

  const rejectClaim = async (itemId, index) => {
    try {
      await api.put(`/items/${itemId}/claim/${index}/reject`);
      toast.success("Claim rejected");
      loadClaims();
    } catch {
      toast.error("Failed to reject claim");
    }
  };

  if (loading) return <p className="p-6">Loading claims...</p>;

  return (
    <div className="p-6 max-w-6xl mx-auto space-y-6">
      <h1 className="text-3xl font-extrabold">
        Claim Review
      </h1>

      {items.length === 0 && (
        <p className="text-gray-500">No claims submitted yet.</p>
      )}

      {items.map(item => (
        <div
          key={item._id}
          className="bg-white p-4 rounded-xl shadow"
        >
          <h2 className="text-xl font-bold mb-2">
            {item.title}
          </h2>

          {item.claims.map((claim, index) => (
            <div
              key={index}
              className="border rounded p-3 mb-3"
            >
              <p className="font-semibold">
                Claim by: {claim.userId.name}
              </p>

              <ul className="mt-2 text-sm list-disc ml-5">
                {claim.answers.map((a, i) => (
                  <li key={i}>{a}</li>
                ))}
              </ul>

              {claim.status === "pending" && (
                <div className="flex gap-2 mt-3">
                  <button
                    onClick={() =>
                      approveClaim(item._id, index)
                    }
                    className="bg-green-600 text-white px-3 py-1 rounded"
                  >
                    Approve
                  </button>

                  <button
                    onClick={() =>
                      rejectClaim(item._id, index)
                    }
                    className="bg-red-600 text-white px-3 py-1 rounded"
                  >
                    Reject
                  </button>
                </div>
              )}

              {claim.status !== "pending" && (
                <p className="mt-2 text-sm text-gray-500">
                  Status: {claim.status.toUpperCase()}
                </p>
              )}
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
