import { useEffect, useState } from "react";
import api from "../services/api";
import toast from "react-hot-toast";

export default function Admin() {
  const [items, setItems] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  /* LOAD DATA */
  const loadData = async () => {
    try {
      const [itemsRes, usersRes] = await Promise.all([
        api.get("/items"),
        api.get("/users")
      ]);

      setItems(itemsRes.data);
      setUsers(usersRes.data);
    } catch {
      toast.error("Failed to load admin data");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  /* ITEM ACTIONS */
  const approveItem = async (id) => {
    await api.put(`/items/${id}/approve`);
    toast.success("Item approved");
    loadData();
  };

  const deleteItem = async (id) => {
  if (!window.confirm("Delete item permanently?")) return;

  try {
    await api.delete(`/items/${id}`);
    toast.success("Item deleted successfully");
    loadData();
  } catch (err) {
    console.error(err);
    toast.error(
      err.response?.data?.message || "Failed to delete item"
    );
  }
};

  /* USER ROLE ACTION */
  const updateRole = async (id, role) => {
    await api.put(`/users/${id}/role`, { role });
    toast.success("Role updated");
    loadData();
  };

  if (loading) return <p className="p-6">Loading admin panel...</p>;

  return (
    <div className="p-6 max-w-7xl mx-auto space-y-10">

      {/* HEADER */}
      <h1 className="text-4xl font-extrabold">Admin Dashboard</h1>

      {/* ================= ITEMS ================= */}
      <section>
        <h2 className="text-2xl font-bold mb-4">Items</h2>

        <div className="space-y-3">
          {items.map(item => (
            <div
              key={item._id}
              className="bg-white p-4 rounded-xl shadow flex justify-between items-center animate-cardFade"
            >
              <div>
                <h3 className="font-bold">{item.title}</h3>
                <p className="text-sm text-gray-500">
                  {item.type} â€¢ {item.status}
                </p>
              </div>

              <div className="flex gap-2">
                {item.status === "pending" && (
                  <button
                    onClick={() => approveItem(item._id)}
                    className="px-3 py-1 bg-green-500 text-white rounded"
                  >
                    Approve
                  </button>
                )}

                <button
                  onClick={() => deleteItem(item._id)}
                  className="px-3 py-1 bg-red-500 text-white rounded"
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* ================= USERS ================= */}
      <section>
        <h2 className="text-2xl font-bold mb-4">User Management</h2>

        <div className="space-y-3">
          {users.map(user => (
            <div
              key={user._id}
              className="bg-white p-4 rounded-xl shadow flex justify-between items-center animate-cardFade"
            >
              <div>
                <p className="font-semibold">{user.name}</p>
                <p className="text-sm text-gray-500">{user.email}</p>
              </div>

              <div className="flex items-center gap-3">
                <span
                  className={`px-3 py-1 text-xs font-bold rounded-full
                    ${user.role === "admin"
                      ? "bg-purple-100 text-purple-700"
                      : "bg-gray-100 text-gray-700"}
                  `}
                >
                  {user.role.toUpperCase()}
                </span>

                {user.role === "admin" ? (
                  <button
                    onClick={() => updateRole(user._id, "user")}
                    className="text-red-600 hover:underline text-sm"
                  >
                    Demote
                  </button>
                ) : (
                  <button
                    onClick={() => updateRole(user._id, "admin")}
                    className="text-blue-600 hover:underline text-sm"
                  >
                    Promote
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
