import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../services/api";
import toast from "react-hot-toast";

export default function ItemDetail() {
  const { id } = useParams();

  const [item, setItem] = useState(null);
  const [loading, setLoading] = useState(true);
  const [answers, setAnswers] = useState([]);
  const [submitting, setSubmitting] = useState(false);

  /* LOAD ITEM */
  useEffect(() => {
    const loadItem = async () => {
      try {
        const res = await api.get(`/items/${id}`);
        setItem(res.data);

        // Prepare answers array
        if (res.data?.verificationQuestions) {
          setAnswers(
            res.data.verificationQuestions.map(() => "")
          );
        }
      } catch {
        toast.error("Item not found");
      } finally {
        setLoading(false);
      }
    };

    loadItem();
  }, [id]);

  /* HANDLE ANSWERS */
  const handleAnswerChange = (index, value) => {
    const updated = [...answers];
    updated[index] = value;
    setAnswers(updated);
  };

  /* SUBMIT CLAIM */
  const submitClaim = async (e) => {
    e.preventDefault();

    if (answers.some((a) => !a.trim())) {
      toast.error("Please answer all questions");
      return;
    }

    try {
      setSubmitting(true);

      // üîê Future API (ready to connect backend)
      await api.post(`/items/${id}/claim`, {
        answers
      });

      toast.success("Claim submitted for verification üîç");
    } catch (err) {
      toast.error(
        err.response?.data?.message || "Failed to submit claim"
      );
    } finally {
      setSubmitting(false);
    }
  };

  /* LOADING */
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-gray-500 text-lg animate-pulse">
          Loading item details...
        </p>
      </div>
    );
  }

  /* NOT FOUND */
  if (!item) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center">
        <p className="text-xl font-semibold text-gray-600">
          Item not found
        </p>
        <Link
          to="/dashboard"
          className="mt-4 text-blue-600 hover:underline"
        >
          ‚Üê Back to dashboard
        </Link>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 to-blue-50 p-6">
      <div className="max-w-4xl mx-auto bg-white rounded-xl shadow-lg p-6 animate-fadeInUp">

        {/* BACK */}
        <Link
          to="/dashboard"
          className="text-sm text-blue-600 hover:underline"
        >
          ‚Üê Back
        </Link>

        {/* IMAGE */}
        {item.image && (
          <img
            src={`http://localhost:5000/${item.image}`}
            alt={item.title}
            className="w-full h-80 object-cover rounded-lg mt-4 mb-6"
          />
        )}

        {/* TITLE */}
        <h1 className="text-3xl font-extrabold text-gray-800">
          {item.title}
        </h1>

        {/* TYPE + STATUS */}
        <div className="flex items-center gap-3 mt-3">
          <span
            className={`px-3 py-1 text-sm font-bold rounded-full
              ${
                item.type === "lost"
                  ? "bg-red-100 text-red-600"
                  : "bg-green-100 text-green-600"
              }`}
          >
            {item.type.toUpperCase()}
          </span>

          <span
            className={`px-3 py-1 text-sm font-bold rounded-full
              ${
                item.status === "approved"
                  ? "bg-green-100 text-green-700"
                  : "bg-yellow-100 text-yellow-700"
              }`}
          >
            {item.status.toUpperCase()}
          </span>
        </div>

        {/* DESCRIPTION */}
        <p className="text-gray-700 mt-6 leading-relaxed">
          {item.description}
        </p>

        {/* CONTACT */}
        <div className="mt-6 border-t pt-4">
          <p className="text-sm text-gray-500">Contact Information</p>
          <p className="text-lg font-semibold text-gray-800 mt-1">
            {item.contact}
          </p>
        </div>

        {/* ================= VERIFY OWNERSHIP ================= */}
        {item.verificationQuestions?.length > 0 &&
          !item.claimed && (
            <form
              onSubmit={submitClaim}
              className="mt-8 border-t pt-6 animate-fadeInUp"
            >
              <h2 className="text-xl font-bold mb-4">
                Verify Ownership üîê
              </h2>

              {item.verificationQuestions.map((q, i) => (
                <input
                  key={i}
                  value={answers[i] || ""}
                  onChange={(e) =>
                    handleAnswerChange(i, e.target.value)
                  }
                  placeholder={q.question}
                  className="border p-3 w-full mb-3 rounded-lg
                    focus:ring-2 focus:ring-blue-400"
                />
              ))}

              <button
                type="submit"
                disabled={submitting}
                className="mt-2 bg-blue-600 text-white px-5 py-2 rounded-lg
                  hover:bg-blue-700 active:scale-95 transition
                  disabled:opacity-60"
              >
                {submitting ? "Submitting..." : "Submit Claim"}
              </button>
            </form>
          )}

        {item.claimed && (
          <p className="mt-6 text-green-600 font-semibold">
            ‚úÖ This item has already been claimed
          </p>
        )}
      </div>
    </div>
  );
}
