import { Link, useLocation, useNavigate } from "react-router-dom";
import { useState } from "react";

export default function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();

  const isLoggedIn = !!localStorage.getItem("token");
  const role = localStorage.getItem("role");
  const name = localStorage.getItem("name");
  const isAdmin = role === "admin";

  const [open, setOpen] = useState(false);

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    localStorage.removeItem("name");
    setOpen(false);
    navigate("/login");
  };

  const NavItem = ({ to, children }) => (
    <Link
      to={to}
      onClick={() => setOpen(false)}
      className={`block px-4 py-2 text-lg font-medium transition
        ${
          location.pathname === to
            ? "text-blue-600"
            : "text-gray-700 hover:text-blue-600"
        }`}
    >
      {children}
    </Link>
  );

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50 animate-slideDown">
      <div className="max-w-6xl mx-auto px-4 py-3 flex justify-between items-center">

        {/* Logo */}
        <Link
          to="/dashboard"
          className="text-2xl font-extrabold tracking-wide hover:scale-105 transition-transform"
        >
          <span className="text-blue-600">we</span>
          <span className="text-gray-900">FOUND</span>
          <span className="text-green-600">it</span>
        </Link>

        {/* Desktop Menu */}
        <div className="hidden md:flex items-center gap-4">

          {/* Public */}
          <DesktopLink to="/about" active={location.pathname === "/about"}>
            About
          </DesktopLink>

          <DesktopLink
            to="/how-it-works"
            active={location.pathname === "/how-it-works"}
          >
            How It Works
          </DesktopLink>

          <DesktopLink to="/contact" active={location.pathname === "/contact"}>
            Contact
          </DesktopLink>

          {!isLoggedIn && (
            <>
              <DesktopLink to="/login" active={location.pathname === "/login"}>
                Login
              </DesktopLink>
              <DesktopLink
                to="/register"
                active={location.pathname === "/register"}
              >
                Register
              </DesktopLink>
            </>
          )}

          {isLoggedIn && (
            <>
              <DesktopLink
                to="/dashboard"
                active={location.pathname === "/dashboard"}
              >
                Dashboard
              </DesktopLink>

              <DesktopLink
                to="/my-posts"
                active={location.pathname === "/my-posts"}
              >
                My Posts
              </DesktopLink>

              {isAdmin && (
                <DesktopLink
                  to="/admin"
                  active={location.pathname === "/admin"}
                >
                  Admin
                </DesktopLink>
              )}

              {/* USER NAME + ADMIN BADGE */}
              <div className="flex items-center gap-2 ml-3">
                <span className="font-semibold text-gray-700">
                  {name || "User"}
                </span>

                {isAdmin && (
                  <span
                    className="px-2 py-0.5 text-xs font-bold rounded-full
                      bg-purple-100 text-purple-700 animate-fadeIn"
                  >
                    ADMIN
                  </span>
                )}
              </div>

              <button
                onClick={logout}
                className="ml-3 px-4 py-2 rounded bg-red-500 text-white
                  hover:bg-red-600 hover:shadow-lg active:scale-95 transition"
              >
                Logout
              </button>
            </>
          )}
        </div>

        {/* Hamburger */}
        <button
          onClick={() => setOpen(!open)}
          className="md:hidden flex flex-col gap-1.5"
        >
          <span className={`hamburger ${open ? "open" : ""}`} />
          <span className={`hamburger ${open ? "open" : ""}`} />
          <span className={`hamburger ${open ? "open" : ""}`} />
        </button>
      </div>

      {/* Mobile Menu */}
      <div
        className={`md:hidden overflow-hidden transition-all duration-300
          ${open ? "max-h-96 opacity-100" : "max-h-0 opacity-0"}`}
      >
        <div className="bg-white border-t py-4 space-y-2 animate-fadeIn">

          {/* Public */}
          <NavItem to="/about">About</NavItem>
          <NavItem to="/how-it-works">How It Works</NavItem>
          <NavItem to="/contact">Contact</NavItem>

          {!isLoggedIn && (
            <>
              <NavItem to="/login">Login</NavItem>
              <NavItem to="/register">Register</NavItem>
            </>
          )}

          {isLoggedIn && (
            <>
              <NavItem to="/dashboard">Dashboard</NavItem>
              <NavItem to="/my-posts">My Posts</NavItem>

              {isAdmin && <NavItem to="/admin">Admin</NavItem>}

              {/* Mobile user badge */}
              <div className="px-4 py-2 flex items-center gap-2">
                <span className="font-semibold text-gray-700">
                  {name || "User"}
                </span>
                {isAdmin && (
                  <span className="px-2 py-0.5 text-xs font-bold rounded-full
                    bg-purple-100 text-purple-700">
                    ADMIN
                  </span>
                )}
              </div>

              <button
                onClick={logout}
                className="block w-full text-left px-4 py-2 text-lg
                  text-red-600 hover:bg-red-50 transition"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

/* Desktop link with underline animation */
function DesktopLink({ to, children, active }) {
  return (
    <Link
      to={to}
      className={`relative px-3 py-2 font-medium transition
        ${
          active
            ? "text-blue-600"
            : "text-gray-700 hover:text-blue-600"
        }`}
    >
      {children}
      <span
        className={`absolute left-0 -bottom-1 h-0.5 bg-blue-600 transition-all duration-300
          ${active ? "w-full" : "w-0 hover:w-full"}`}
      />
    </Link>
  );
}
