export default function HowItWorks() {
  return (
    <div className="min-h-screen p-6 animate-fadeInUp">
      <h1 className="text-3xl font-bold mb-6">How It Works</h1>

      <ol className="space-y-4 text-gray-700">
        <li>1️⃣ Register or login to weFOUNDit</li>
        <li>2️⃣ Post a lost or found item</li>
        <li>3️⃣ Admin reviews and approves posts</li>
        <li>4️⃣ People connect and recover items</li>
      </ol>
    </div>
  );
}
