import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import api from "../services/api";
import toast from "react-hot-toast";

export default function ItemDetail() {
  const { id } = useParams();
  const [item, setItem] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [claimSubmitted, setClaimSubmitted] = useState(false);

  const isLoggedIn = Boolean(localStorage.getItem("token"));

  useEffect(() => {
    const loadItem = async () => {
      try {
        const res = await api.get(`/items/${id}`);
        setItem(res.data);

        if (res.data.verificationQuestions?.length > 0) {
          setAnswers(res.data.verificationQuestions.map(() => ""));
        }
      } catch {
        toast.error("Item not found");
      } finally {
        setLoading(false);
      }
    };

    loadItem();
  }, [id]);

  const submitClaim = async (e) => {
    e.preventDefault();

    if (answers.some((a) => !a.trim())) {
      toast.error("Please answer all questions");
      return;
    }

    try {
      await api.post(`/items/${id}/claim`, { answers });
      toast.success("Claim submitted for review ‚úÖ");
      setClaimSubmitted(true);
    } catch (err) {
      toast.error(
        err.response?.data?.message || "Failed to submit claim"
      );
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <p className="text-gray-500 animate-pulse">
          Loading item...
        </p>
      </div>
    );
  }

  if (!item) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center">
        <p className="text-xl font-semibold">
          Item not found
        </p>
        <Link to="/dashboard" className="text-blue-600 mt-3">
          ‚Üê Back
        </Link>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-4xl mx-auto bg-white p-6 rounded-xl shadow animate-fadeInUp">

        {/* BACK */}
        <Link to="/dashboard" className="text-blue-600 text-sm">
          ‚Üê Back
        </Link>

        {/* IMAGE */}
        {item.image && (
          <img
            src={`http://localhost:5000/${item.image}`}
            alt={item.title}
            className="w-full h-80 object-cover rounded-lg mt-4"
          />
        )}

        {/* TITLE */}
        <h1 className="text-3xl font-bold mt-4">
          {item.title}
        </h1>

        {/* TYPE */}
        <div className="mt-3">
          <span
            className={`px-3 py-1 text-sm font-bold rounded-full ${
              item.type === "lost"
                ? "bg-red-100 text-red-600"
                : "bg-green-100 text-green-600"
            }`}
          >
            {item.type.toUpperCase()}
          </span>
        </div>

        {/* DESCRIPTION */}
        <p className="mt-4 text-gray-700">
          {item.description}
        </p>

        {/* üîê CONTACT (Protected) */}
        <div className="mt-6 border-t pt-4">
          <p className="text-sm text-gray-500">
            Contact Information
          </p>

          {item.contact ? (
            <p className="text-lg font-semibold text-gray-800 mt-1">
              {item.contact}
            </p>
          ) : (
            <p className="text-red-500 mt-1">
              Contact hidden until ownership is verified
            </p>
          )}
        </div>

        {/* üîê CLAIM FORM */}
        {isLoggedIn &&
          item.verificationQuestions?.length > 0 &&
          !item.claimed &&
          !claimSubmitted && (
            <form
              onSubmit={submitClaim}
              className="mt-8 border-t pt-6"
            >
              <h2 className="text-xl font-bold mb-4">
                Verify Ownership
              </h2>

              {item.verificationQuestions.map((q, i) => (
                <input
                  key={i}
                  value={answers[i]}
                  onChange={(e) => {
                    const copy = [...answers];
                    copy[i] = e.target.value;
                    setAnswers(copy);
                  }}
                  placeholder={q.question}
                  className="border p-2 w-full mb-3 rounded
                    focus:ring-2 focus:ring-blue-400"
                />
              ))}

              <button
                className="bg-blue-600 text-white px-4 py-2 rounded
                  hover:bg-blue-700 transition"
              >
                Submit Claim
              </button>
            </form>
          )}

        {/* CLAIM SUBMITTED */}
        {claimSubmitted && (
          <p className="mt-6 text-yellow-600 font-semibold">
            ‚è≥ Your claim is under review
          </p>
        )}

        {/* ITEM CLAIMED */}
        {item.claimed && (
          <p className="mt-6 text-green-600 font-semibold">
            ‚úî This item has been claimed
          </p>
        )}
      </div>
    </div>
  );
}
