import { useEffect, useState } from "react";
import api from "../services/api";
import PostItem from "../components/PostItem";

export default function Dashboard() {
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState("");

  // Load items (with optional filter)
  const loadItems = () => {
    api
      .get("/items" + (filter ? `?type=${filter}` : ""))
      .then(res => setItems(res.data))
      .catch(() => alert("Unauthorized or server error"));
  };

  useEffect(() => {
    loadItems();
  }, [filter]);

  const logout = () => {
    localStorage.removeItem("token");
    window.location.href = "/login";
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Lost & Found Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-600 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {/* Post Lost / Found */}
      <PostItem onPosted={loadItems} />

      {/* Filter */}
      <div className="mb-6">
        <label className="mr-2 font-semibold">Filter:</label>
        <select
          className="border p-2 rounded"
          value={filter}
          onChange={e => setFilter(e.target.value)}
        >
          <option value="">All</option>
          <option value="lost">Lost</option>
          <option value="found">Found</option>
        </select>
      </div>

      {/* Items Grid */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {items.length === 0 && (
          <p className="text-gray-500">No items found</p>
        )}

        {items.map(item => (
          <div
            key={item._id}
            className="bg-white border rounded p-4 shadow"
          >
            {item.image && (
              <img
                src={`http://localhost:5000/${item.image}`}
                alt="Item"
                className="h-40 w-full object-cover mb-2 rounded"
              />
            )}

            <h3 className="font-bold text-lg">{item.title}</h3>
            <p className="text-gray-700">{item.description}</p>

            <p className="text-sm text-gray-600 mt-2">
              Type: <span className="font-semibold">{item.type}</span>
            </p>

            <p className="text-sm text-gray-600">
              Contact: {item.contact}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
