import { Link, useLocation, useNavigate } from "react-router-dom";

export default function Navbar() {
  const location = useLocation();
  const navigate = useNavigate();
  const isLoggedIn = !!localStorage.getItem("token");

  const logout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="bg-white shadow-md sticky top-0 z-50 animate-slideDown">
      <div className="max-w-6xl mx-auto px-4 py-3 flex justify-between items-center">

        {/* Logo */}
        <Link
          to="/"
          className="text-2xl font-extrabold text-blue-600 tracking-wide hover:scale-105 transition-transform"
        >
          Lost<span className="text-gray-800">&</span>Found
        </Link>

        {/* Navigation */}
        <div className="flex items-center gap-4">

          {!isLoggedIn && (
            <>
              <NavLink to="/login" active={isActive("/login")}>
                Login
              </NavLink>

              <NavLink to="/register" active={isActive("/register")}>
                Register
              </NavLink>
            </>
          )}

          {isLoggedIn && (
            <>
              <NavLink to="/" active={isActive("/")}>
                Dashboard
              </NavLink>

              <NavLink to="/admin" active={isActive("/admin")}>
                Admin
              </NavLink>

              <button
                onClick={logout}
                className="ml-2 px-4 py-2 rounded bg-red-500 text-white font-semibold
                  hover:bg-red-600 hover:shadow-lg active:scale-95 transition-all"
              >
                Logout
              </button>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}

/* ðŸ”¹ Animated Nav Link */
function NavLink({ to, children, active }) {
  return (
    <Link
      to={to}
      className={`relative px-3 py-2 font-medium transition-all
        ${
          active
            ? "text-blue-600"
            : "text-gray-700 hover:text-blue-600"
        }`}
    >
      {children}

      {/* Underline animation */}
      <span
        className={`absolute left-0 -bottom-1 h-0.5 bg-blue-600 transition-all duration-300
          ${active ? "w-full" : "w-0 group-hover:w-full"}`}
      />
    </Link>
  );
}
