import { useEffect, useState } from "react";
import api from "../services/api";
import { useNavigate } from "react-router-dom";

export default function UserManagement() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const loadUsers = async () => {
    try {
      const res = await api.get("/users");
      setUsers(res.data);
    } catch (err) {
      if (err.response?.status === 403) {
        navigate("/dashboard");
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadUsers();
  }, []);

  const promote = async (id) => {
    await api.put(`/users/${id}/promote`);
    loadUsers();
  };

  const demote = async (id) => {
    const ok = window.confirm(
      "This will remove admin privileges.\nContinue?"
    );
    if (!ok) return;

    await api.put(`/users/${id}/demote`);
    loadUsers();
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 animate-fadeInUp">
      <h1 className="text-3xl font-extrabold mb-6">
        User Management
      </h1>

      <div className="bg-white rounded-xl shadow p-4">
        {loading ? (
          <p className="text-gray-500">Loading users...</p>
        ) : users.length === 0 ? (
          <p className="text-gray-500">No users found</p>
        ) : (
          <div className="space-y-3">
            {users.map((user, index) => (
              <div
                key={user._id}
                style={{ animationDelay: `${index * 50}ms` }}
                className="flex justify-between items-center border rounded-lg p-4
                  hover:shadow transition animate-cardFade"
              >
                <div>
                  <p className="font-bold text-gray-800">
                    {user.name}
                  </p>
                  <p className="text-sm text-gray-500">
                    {user.email}
                  </p>
                  <span
                    className={`inline-block mt-1 px-2 py-0.5 text-xs font-bold rounded-full
                      ${
                        user.role === "admin"
                          ? "bg-purple-100 text-purple-700"
                          : "bg-gray-100 text-gray-600"
                      }`}
                  >
                    {user.role.toUpperCase()}
                  </span>
                </div>

                <div className="flex gap-2">
                  {user.role === "user" && (
                    <button
                      onClick={() => promote(user._id)}
                      className="px-4 py-1.5 rounded bg-green-600 text-white
                        hover:bg-green-700 active:scale-95 transition"
                    >
                      Promote
                    </button>
                  )}

                  {user.role === "admin" && (
                    <button
                      onClick={() => demote(user._id)}
                      className="px-4 py-1.5 rounded bg-red-500 text-white
                        hover:bg-red-600 active:scale-95 transition"
                    >
                      Demote
                    </button>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
